import React, { useState } from 'react';
import axios from 'axios';

const RandomDataGenerator = () => {
    const [region, setRegion] = useState('us');  // По умолчанию установим 'us'
    const [errors, setErrors] = useState(0);
    const [seed, setSeed] = useState('');
    const [page, setPage] = useState(1);
    const [data, setData] = useState([]);

    const handleGenerate = async () => {
        try {
            const response = await axios.post('http://localhost:3000/generate', {
                region,
                errors,
                seed,
                page,
            });
            setData(response.data);
        } catch (error) {
            console.error('Error generating data:', error);
        }
    };

    return (
        <div>
            <h1>Генератор случайных данных</h1>

            {/* Выпадающий список для выбора региона */}
            <label htmlFor="region">Выберите регион:</label>
            <select
                id="region"
                value={region}
                onChange={(e) => setRegion(e.target.value)}
            >
                <option value="us">США</option>
                <option value="ru">Россия</option>
                <option value="de">Германия</option>
            </select>

            <input
                type="number"
                placeholder="Количество ошибок"
                value={errors}
                onChange={(e) => setErrors(e.target.value)}
            />
            <input
                type="text"
                placeholder="Сид"
                value={seed}
                onChange={(e) => setSeed(e.target.value)}
            />
            <input
                type="number"
                placeholder="Номер страницы"
                value={page}
                onChange={(e) => setPage(e.target.value)}
            />
            <button onClick={handleGenerate}>Сгенерировать</button>

            <h2>Сгенерированные данные:</h2>
            <ul>
                {data.map((item) => (
                    <li key={item.uuid}>
                        {item.name}, {item.address}, {item.phone}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default RandomDataGenerator;
