import React, { useState, useEffect, useCallback, useRef } from 'react';
import axios from 'axios';
import './RandomDataGenerator.css';

const RandomDataGenerator = () => {
    const [region, setRegion] = useState('');
    const [errors, setErrors] = useState(0);
    const [seed, setSeed] = useState('');
    const [page, setPage] = useState(1); // Начальная страница
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [hasMore, setHasMore] = useState(true); // Проверка на наличие данных для загрузки

    const tableContainerRef = useRef(null); // Реф для контейнера таблицы

    // Функция загрузки данных с сервера
    const loadData = async (pageToLoad) => {
        try {
            setLoading(true);
            const response = await axios.post('http://localhost:3000/generate', {
                region,
                errors,
                seed,
                page: pageToLoad, // Передаем номер страницы
            });
            if (response.data.length > 0) {
                setData((prevData) => [...prevData, ...response.data]);
                setPage(pageToLoad + 1);
            } else {
                setHasMore(false); // Если данных больше нет
            }
        } catch (error) {
            console.error('Error loading data:', error);
        } finally {
            setLoading(false);
        }
    };

    // Функция для генерации данных при нажатии кнопки
    const handleGenerate = async () => {
        setData([]); // Очищаем предыдущие данные
        setPage(1);  // Сбрасываем страницу на 1
        setHasMore(true); // Сбрасываем флаг hasMore
        loadData(1); // Загружаем первую порцию данных
    };

    // Генерация случайного seed
    const generateRandomSeed = async () => {
        try {
            const response = await axios.get('http://localhost:3000/random-seed');
            setSeed(response.data.seed); // Устанавливаем случайный seed в состояние
        } catch (error) {
            console.error('Error generating random seed:', error);
        }
    };

    // Обработчик скролла для бесконечной прокрутки
    const handleScroll = useCallback(() => {
        const container = tableContainerRef.current;
        if (container.scrollTop + container.clientHeight >= container.scrollHeight && !loading && hasMore) {
            loadData(page); // Загружаем следующую страницу
        }
    }, [page, loading, hasMore]);

    useEffect(() => {
        const container = tableContainerRef.current;
        container.addEventListener('scroll', handleScroll);
        return () => container.removeEventListener('scroll', handleScroll);
    }, [handleScroll]);

    return (
        <div className="container mt-5">
            <h1 className="mb-4">Random Data Generator</h1>

            <div className="form-group">
                <div className="input-group">
                    <select
                        className="form-control"
                        value={region}
                        onChange={(e) => setRegion(e.target.value)}
                    >
                        <option value="">Select Language</option>
                        <option value="en">English</option>
                        <option value="de">Deutsch</option>
                        <option value="pl">Polski</option>
                    </select>
                </div>

                <div className="input-group">
                    <input
                        type="range"
                        className="slider"
                        min="0"
                        max="10"
                        step="0.25"
                        value={errors}
                        onChange={(e) => setErrors(e.target.value)}
                    />
                    <input
                        type="number"
                        className="form-control"
                        placeholder="Error Count"
                        min="0"
                        max="1000"
                        value={errors}
                        onChange={(e) => setErrors(e.target.value)}
                        inputMode="decimal"
                    />
                </div>

                <div className="input-group">
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Seed"
                        value={seed}
                        readOnly
                    />
                    <button
                        className="btn btn-secondary ml-2"
                        onClick={generateRandomSeed}
                    >
                        Random Seed
                    </button>
                </div>

                <div className="button-container">
                    <button
                        onClick={handleGenerate}
                        className="btn btn-primary"
                    >
                        Generate
                    </button>
                </div>
            </div>

            <div className="table-container" ref={tableContainerRef}>
                <h2>Generated Data:</h2>

                <table className="table table-striped">
                    <thead className="thead-dark">
                    <tr>
                        <th>#</th>
                        <th>UUID</th>
                        <th>Name</th>
                        <th>Address</th>
                        <th>Phone</th>
                    </tr>
                    </thead>
                    <tbody>
                    {data.map((item, index) => (
                        <tr key={item.uuid}>
                            <td>{index + 1}</td>
                            <td>{item.uuid}</td>
                            <td>{item.name}</td>
                            <td>{item.address}</td>
                            <td>{item.phone}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>

                {loading && <div>Loading more data...</div>}
                {!hasMore && <div>No more data available</div>}
            </div>
        </div>
    );
};

export default RandomDataGenerator;
