import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './RandomDataGenerator.css';

const RandomDataGenerator = () => {
    const [region, setRegion] = useState('');
    const [errors, setErrors] = useState(0);
    const [seed, setSeed] = useState('');
    const [page, setPage] = useState(1);
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(false); // Для индикатора загрузки
    const tableRef = useRef(null); // Ссылка на таблицу для отслеживания скролла

    // Обработчик кнопки "Generate"
    const handleGenerate = async () => {
        try {
            const response = await axios.post('http://localhost:3000/generate', {
                region,
                errors,
                seed,
                page,
            });
            setData(response.data); // Заносим первые 20 записей
        } catch (error) {
            console.error('Error generating data:', error);
        }
    };

    // Генерация случайного seed
    const generateRandomSeed = async () => {
        try {
            const response = await axios.get('http://localhost:3000/random-seed');
            setSeed(response.data.seed);
        } catch (error) {
            console.error('Error generating random seed:', error);
        }
    };

    // Функция для загрузки новой порции данных
    const loadMoreData = async () => {
        if (loading) return; // Не загружать, если уже идет загрузка
        setLoading(true);
        try {
            const response = await axios.post('http://localhost:3000/generate', {
                region,
                errors,
                seed,
                page: page + 1, // Увеличиваем номер страницы
            });
            setData(prevData => [...prevData, ...response.data]); // Добавляем новые записи к уже существующим
            setPage(prevPage => prevPage + 1); // Увеличиваем номер страницы
        } catch (error) {
            console.error('Error loading more data:', error);
        } finally {
            setLoading(false);
        }
    };

    // Проверяем, достиг ли пользователь конца таблицы
    const handleScroll = () => {
        const table = tableRef.current;
        if (table.scrollTop + table.clientHeight >= table.scrollHeight - 5) {
            // Если скролл почти до конца, подгружаем следующую порцию данных
            loadMoreData();
        }
    };

    // Привязываем обработчик события скролла
    useEffect(() => {
        const table = tableRef.current;
        table.addEventListener('scroll', handleScroll);
        return () => {
            table.removeEventListener('scroll', handleScroll);
        };
    }, [page, loading]);

    return (
        <div className="container mt-5">
            <h1 className="mb-4">Random Data Generator</h1>

            <div className="form-group">
                <div className="input-group">
                    <select
                        className="form-control"
                        value={region}
                        onChange={(e) => setRegion(e.target.value)}
                    >
                        <option value="">Select Language</option>
                        <option value="en">English</option>
                        <option value="de">Deutsch</option>
                        <option value="pl">Polski</option>
                    </select>
                </div>

                <div className="input-group">
                    <input
                        type="range"
                        className="slider"
                        min="0"
                        max="10"
                        step="0.25"
                        value={errors}
                        onChange={(e) => setErrors(e.target.value)}
                    />
                    <input
                        type="number"
                        className="form-control"
                        placeholder="Error Count"
                        min="0"
                        max="1000"
                        value={errors}
                        onChange={(e) => setErrors(e.target.value)}
                        inputMode="decimal"
                    />
                </div>

                <div className="input-group">
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Seed"
                        value={seed}
                        readOnly
                    />
                    <button
                        className="btn btn-secondary ml-2"
                        onClick={generateRandomSeed}
                    >
                        Random Seed
                    </button>
                </div>

                <div className="input-group">
                    <input
                        type="number"
                        className="form-control"
                        placeholder="Page Number"
                        value={page}
                        onChange={(e) => setPage(e.target.value)}
                    />
                </div>

                <div className="button-container">
                    <button
                        onClick={handleGenerate}
                        className="btn btn-primary"
                    >
                        Generate
                    </button>
                </div>
            </div>

            <div className="table-container" ref={tableRef}>
                <h2>Generated Data:</h2>

                <table className="table table-striped">
                    <thead className="thead-dark">
                    <tr>
                        <th>#</th>
                        <th style={{ width: '20%' }}>UUID</th>
                        <th>Name</th>
                        <th>Address</th>
                        <th>Phone</th>
                    </tr>
                    </thead>
                    <tbody>
                    {data.map((item, index) => (
                        <tr key={item.uuid}>
                            <td>{index + 1}</td>
                            <td>{item.uuid}</td>
                            <td>{item.name}</td>
                            <td>{item.address}</td>
                            <td>{item.phone}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
            {loading && <div>Loading...</div>} {/* Индикатор загрузки */}
        </div>
    );
};

export default RandomDataGenerator;
